                <script type="text/javascript">    
                    function DiemdanhClick(code,myRadio) {          
                    $.ajax({
                    url: '/site/diemdanhclick',
                    type: 'post',
                    data: {code: code},
                    success: function (data) {
                    var str=data.replace(/"/g, '');
                    var res = str.split(":");
                    if(res[0] == 'false'){
                    alert("Đăng nhập trước khi điểm danh.");
                    }              
                    if(res[0] == 'false2'){
                    alert("Hôm nay bạn đã điểm danh. Vui lòng quay lại ngày mai.");
                    }
                    if(res[0] == 'false1'){
                    alert("Lổi không tồn tại user.");
                    }
                    if(res[0] == 'false3'){
                    alert("Không check được điểm danh. Liên hệ với nhân viên để được hỗ trợ.");
                    }
                    if(res[0] == 'true'){                  
                    $('#addimg').before('<img style="position: absolute; top:-4px; right:2px; width: 10px; height:10px;" src="/assets/632a3271/images/check.png" />');
                    $('#addimg1').before('<img style="position: absolute; top:-4px; right:2px; width: 10px; height:10px;" src="/assets/632a3271/images/check.png" />');
                    $('#diemhientai').html("<span id='diemhientai' style='color:#aa5915'>"+res[1]+"</span>");
                    $('#diemhientai1').html("<span id='diemhientai1' style='color:#aa5915'>"+res[1]+"</span>");
                    $('.linkcheck').remove();
                    $('.linkcheck1').remove();
                    if(res[1]>33){                      
                    $('#imgaeslist').after('<div class="linkcheck" style="width:100%;">&nbsp;</div>'); 
                    $('#imgaeslist1').after('<div class="linkcheck1" style="width:100%;">&nbsp;</div>'); 
                    }else{
                    if(res[1]<8){ var luong=5.4;}
                    if(res[1]<16 && res[1]>7){ var luong=8; }
                    if(res[1]<22 && res[1]>15){var luong=9;}
                    if(res[1]==22){ var luong=9.2;}
                    if(res[1]<30 && res[1]>22){ var luong=9.3;}
                    if(res[1]<34 && res[1]>29){ var luong=9.4;}
                    if(res[1]>6){$('#imgaeslist').after('<div class="diem1">&nbsp;</div>');$('#imgaeslist1').after('<div class="diem1">&nbsp;</div>');}
                    if(res[1]>14){$('#imgaeslist').after('<div class="diem2">&nbsp;</div>');$('#imgaeslist1').after('<div class="diem2">&nbsp;</div>');}
                    if(res[1]>21){$('#imgaeslist').after('<div class="diem3">&nbsp;</div>');$('#imgaeslist1').after('<div class="diem3">&nbsp;</div>');}
                    if(res[1]>29){$('#imgaeslist').after('<div class="diem4">&nbsp;</div>');$('#imgaeslist1').after('<div class="diem4">&nbsp;</div>');}
                    $('#imgaeslist').after('<div class="linkcheck" style="width:'+res[1]*luong+'px;">&nbsp;</div>'); 
                    $('#imgaeslist1').after('<div class="linkcheck1" style="width:'+res[1]*luong+'px;">&nbsp;</div>'); 
                    $('#diemdanhtrongthang').html("<span id='diemdanhtrongthang'>"+res[2]+"</span>");
                    $('#diemdanhtrongthang1').html("<span id='diemdanhtrongthang1'>"+res[2]+"</span>");
                    $('#diemdanhtrongthang2').html("<span id='diemdanhtrongthang'>"+res[2]+"</span>");
                    $('#diemdanhtrongthang3').html("<span id='diemdanhtrongthang1'>"+res[2]+"</span>");
                    }   
                    alert("Điểm danh thành công.");
                    }              
                    }
                    });
                    }

                    function DoiquaClick(code,myRadio) {      

                    var r=confirm("Bạn có thực sự muốn đổi quà!");
                    if(r==true){
                    $.ajax({
                    url: '/site/doiquaclick',
                    type: 'post',
                    data: {code: code},
                    success: function (data) {
                    var str=data.replace(/"/g, '');
                    var res = str.split(":");
                    if(res[0] == 'false'){
                    alert("Đăng nhập trước khi đổi quà.");
                    }
                    if(res[0] == 'false1'){
                    alert("Lổi không tồn tại user.");
                    }
                    if(res[0] == 'false2'){
                    alert("Bạn không có trong danh sách điểm danh. Liên hệ với nhân viên để được hỗ trợ.");
                    }              
                    if(res[0] == 'false3'){
                    alert("Bạn chưa đủ điểm để đổi quà.");
                    }
                    if(res[0] == 'false4'){
                    alert("Lổi trong quá trình đổi quà. Liên hệ với nhân viên để được hỗ trợ.");
                    }
                    if(res[0] == 'false5'){
                    alert("Error check .");
                    }
                    if(res[0] == 'true'){
                    if(res[1]>33){                      
                    $('#imgaeslist').after('<div class="linkcheck" style="width:100%;">&nbsp;</div>'); 
                    $('#imgaeslist1').after('<div class="linkcheck1" style="width:100%;">&nbsp;</div>'); 
                    }else{
                    if(res[1]<8){ var luong=5.4;}
                    if(res[1]<16 && res[1]>7){ var luong=8;}
                    if(res[1]<22 && res[1]>15){var luong=9;}
                    if(res[1]==22){ var luong=9.2; }
                    if(res[1]<30 && res[1]>22){ var luong=9.3;}
                    if(res[1]<34 && res[1]>29){ var luong=9.4;}
                    if(res[1]<7){
                    $('.diem1').remove();
                    $('.diem2').remove();
                    $('.diem3').remove();
                    $('.diem4').remove();
                    }
                    if(res[1]<15&&res[1]>6){                          
                    $('.diem2').remove();
                    $('.diem3').remove();
                    $('.diem4').remove();
                    }
                    if(res[1]<22&&res[1]>14){                         
                    $('.diem3').remove();
                    $('.diem4').remove();
                    }
                    if(res[1]<30&&res[1]>21){ 
                    $('.diem4').remove();
                    } 
                    $('.linkcheck').remove();
                    $('.linkcheck1').remove();
                    $('#imgaeslist').after('<div class="linkcheck" style="width:'+res[1]*luong+'px;">&nbsp;</div>');                       
                    $('#imgaeslist1').after('<div class="linkcheck" style="width:'+res[1]*luong+'px;">&nbsp;</div>'); 
                    } 
                    $('#diemhientai').html("<span id='diemhientai' style='color:#aa5915'>"+res[1]+"</span>");
                    $('#diemhientai1').html("<span id='diemhientai1' style='color:#aa5915'>"+res[1]+"</span>");
                    alert("Đổi quà thành công.");
                    }              
                    }
                    });
                    }


                    }
